import {DOCUMENT} from '@angular/common';
import { Component, Inject, OnInit } from '@angular/core';
import {PaginasService} from 'src/app/servicios/paginas.service';

@Component({
  selector: 'app-votos',
  templateUrl: './votos.component.html',
  styleUrls: ['./votos.component.css']
})
export class VotosComponent implements OnInit{

    candidatos1:any;
    candidatos2:any;
    infoVotante:any;

    constructor(private servicioPaginas: PaginasService, @Inject(DOCUMENT) private document: Document){ 
    }

    ngOnInit(): void { 
        this.servicioPaginas.mostrarCandidatos().subscribe((data) =>{
           this.candidatos1=data
           this.candidatos2=data
       });
    }

    votar():void{
        let infoMesa = {"inscNumber": "1"};
        let identif = (<HTMLInputElement>document.getElementById("identif")).value
        console.log("dentro del componente \n" + identif)
        this.servicioPaginas.votar(identif, infoMesa, this.infoVotante)
    }
}
