import {HttpClient} from '@angular/common/http';
import { Injectable } from '@angular/core';
import {Router} from '@angular/router';
import {Observable} from 'rxjs';
import {Candidato} from '../modelos/candidato.model';
import {Mesa} from '../modelos/mesa.model';
import {Voto} from '../modelos/voto.model';

@Injectable({
  providedIn: 'root'
})
export class PaginasService {

    url:string="http://localhost:8600";
    constructor(private http: HttpClient, private router: Router) { }

    mostrarVotos(): Observable<Voto>{

        return this.http.get<Voto>(this.url+"/data/votos");
    }

  mostrarCandidatos(): Observable<Candidato>{
      return this.http.get<any>(this.url+"/data/candidatos");
  }

  votar(identifCand: String, infoMesa: Mesa, infoVotante:String){

      let mesaId:any;

      console.log("dentro del servicio")
      console.log(identifCand)
      console.log(infoMesa)
      console.log("mesaId="+mesaId)

      this.http.post<Mesa>(`${this.url}/data/addMesa/candidato/${identifCand}`, infoMesa)
      this.http.get<Mesa>(this.url+"/data/mesas").subscribe((data)=>{
          if(infoMesa._id==data._id){
            mesaId = data._id
          }
      })
      return this.http.post<Candidato>(`${this.url}/data/addVoto/mesa/${mesaId}/candidato/${identifCand}`, infoVotante)
  }

}
