import { Component, ElementRef, OnInit, QueryList, ViewChild, ViewChildren } from '@angular/core';
import {SeguridadService} from 'src/app/servicios/seguridad.service';

@Component({
  selector: 'gestion-usuarios',
  templateUrl: './gestion-usuarios.component.html',
  styleUrls: ['./gestion-usuarios.component.css']
})
export class GestionUsuariosComponent implements OnInit{

    data:any;
    @ViewChild("idUser") idUser: ElementRef;
    @ViewChild("rolData") idRol: ElementRef;
    @ViewChild("passwordData") passwdUser: ElementRef;
    @ViewChild("cuento") indic:ElementRef;
    @ViewChildren("emailUser") emailUsers: QueryList<ElementRef>;


    constructor(private miServicioSeguridad: SeguridadService){}

    ngOnInit(): void {
        this.miServicioSeguridad.listarUsers().subscribe(
            (data)=>{
                this.data=data
            }
        );
    }

    guardar(): void{
        let idUsuario = this.idUser.nativeElement.value;
        let idRol = this.idRol.nativeElement.value;
        let passwd = this.passwdUser.nativeElement.value;

        let indice = this.indic.nativeElement.textContent; 

        console.log(indice);
        console.log(this.emailUsers.filter(data => data.nativeElement.textContent));
//        this.miServicioSeguridad.actualizarUsuario(idUsuario, idRol, passwd)
    }

    eliminar():void{
        let idUsuario = this.idUser.nativeElement.textContent;
        console.log(idUsuario)
        //this.miServicioSeguridad.eliminarUsuario(idUsuario);
    }

}
<div>
    <div>
        <h2>Gestion de Usuarios</h2>
    </div>
    <div>
        <div *ngFor="let datos of data; let i=index" class="users">
            <div>
                <p #emailUser>{{datos.email}}</p>
                <p>{{datos.passwd}}</p>
                <p>{{datos.rol.rolName}}</p>
                <p>{{i}}</p>
                <button>Editar</button>
                <div>
                    <div><label for="email"></label></div>
                    <div><input type="text" value="{{datos.email}}" #emailData></div>
                    <div><label for="passwd"></label></div>
                    <div><input type="text" value="{{datos.passwd}}" #passwordData></div>
                    <div><label for="rol"></label></div>
                    <div>
                        <select name="rol" #rolData>
                            <option value="{{datos.rol._id}}" style="background-color: blue; color:#fff;" >{{datos.rol.rolName}}</option>
                            <option>Superadmin</option>
                            <option>Administrador</option>
                            <option>Jurado</option>
                            <option>Veedor</option>
                            <option>Elector</option>
                            <option>Registrador Nacional</option>
                            <option>Secretario General</option>
                            <option>Secretario Privado</option>
                            <option>Gerencia</option>
                            <option>Talento Humano</option>
                        </select>
                    </div>
                    <div>
                        <input type="hidden" value="{{datos._id}}" #idUser>
                        <button (click)="guardar()">Guardar</button>
                        <button (click)="eliminar()">Eliminar</button>
                    </div>
                </div>
                <hr>
            </div>
        </div>
    </div>

</div>
