import { Component, OnInit } from '@angular/core';
import {SeguridadService} from 'src/app/servicios/seguridad.service';

@Component({
  selector: 'app-perfil',
  templateUrl: './perfil.component.html',
  styleUrls: ['./perfil.component.css']
})
export class PerfilComponent implements OnInit{

    email:any;
    passwd:any;
    rol:any;

    constructor(private miServicioSeguridad: SeguridadService){}

    ngOnInit(): void {
       let usuario = this.miServicioSeguridad.getUsuario();
       usuario.subscribe((data) => {
           this.email = data.email;
           this.passwd = data.passwd;
           this.rol = data.rol;
       });
    }

    desplegar(){
        console.log("hola")
    }
}
<app-menu></app-menu>

<h2>Perfil</h2>

<div>
    <table>
        <tr>
            <td>Correo Electronico: </td>
            <td>{{email}}</td>
        </tr>
        <tr>
            <td>passwd: </td>
            <td> <input type="password" value="{{passwd}}"> </td>
            <td> <button (click)="desplegar()">Cambiar</button></td>           
        </tr>
        <tr>
            <td></td>
            <td>
                <div>
                    <tr>
                        <td> <input type="password" placeholder="Nueva Contraseña"> </td>         
                    </tr>
                    <tr>
                        <td> <input type="password" placeholder="Repite la Contraseña"> </td>         
                    </tr>
                    <tr>
                        <td><div> <button>Cambiar</button><button>Cancelar</button> </div></td>         
                    </tr>
                </div>
            </td>
        </tr>    
        <tr>
            <td>Rol: </td>
            <td>{{rol}}</td>
        </tr>
    </table>
</div>
