import {DOCUMENT} from '@angular/common';
import { Component, ElementRef, Inject, OnInit, ViewChild } from '@angular/core';
import {PaginasService} from 'src/app/servicios/paginas.service';

@Component({
  selector: 'app-votos',
  templateUrl: './votos.component.html',
  styleUrls: ['./votos.component.css']
})
export class VotosComponent implements OnInit{

    candidatos:any;
    @ViewChild("infoIdentif") infoVotante:ElementRef;


    constructor(private servicioPaginas: PaginasService){ 
    }

    ngOnInit(): void { 
        this.servicioPaginas.mostrarCandidatos().subscribe((data) =>{
           this.candidatos=data
       });
    }

    votar():void{

        let infoMesa = {"inscNumber": "1"};
        let identif = this.infoVotante.nativeElement.value;
        console.log("dentro del componente \n" + identif)
        this.servicioPaginas.votar(identif, infoMesa)
    }
}


<div class="tabla">
    <div *ngFor="let info of candidatos">
        <div class="pageCand">
            <div class="card border-left-primary shadow h-100 py-2 contenido">
                <div class="card-body tarjeta">
                    <p>{{info.candName}} {{info.candLastName}}</p>
                    <p>{{info.partyCand.partyName}}</p>
                    <p>"{{info.partyCand.slogan}}"</p>
                    <div class="btn-votar">
                        <input type="hidden" value="{{info.candId}}" #infoIdentif>
                        <input class="d-none d-sm-inline-block btn btn-sm btn-primary shadow-sm btn-votar" type="submit" value="Votar" (click)="votar()">
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
