import { Component, ElementRef, Renderer2, ViewChild } from '@angular/core';
import {Router} from '@angular/router';
import {SeguridadService} from 'src/app/servicios/seguridad.service';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent {
    @ViewChild('menuContainer') menuContainer:ElementRef;
    @ViewChild('configElect') configElect:ElementRef;

    cont:boolean=false;
    cont2:boolean=true;

    constructor(private miServicioSeguridad: SeguridadService, private router: Router, private renderer: Renderer2){}

    salir():void{
        this.router.navigate(['login'])
        this.miServicioSeguridad.logout();
    }

    desplegar(){
        if(this.cont==false){
            this.renderer.removeClass(this.menuContainer.nativeElement, "content");
            this.renderer.addClass(this.menuContainer.nativeElement, "content2")
            this.cont=true;
        }else if(this.cont==true){
            this.renderer.removeClass(this.menuContainer.nativeElement, "content2")
            this.renderer.addClass(this.menuContainer.nativeElement, "content")
            this.cont=false;
        }

        if(this.cont2==true){
            this.renderer.removeClass(this.configElect.nativeElement, "configElect");
            this.cont2=false;
        }else if(this.cont2==false){
            this.renderer.addClass(this.configElect.nativeElement, "configElect");
            this.cont2=true;
        }
    }
}
<div>
    <button class="btnMenu" (click)="desplegar()">Menu</button>
    <div #menuContainer class="content">
        <ul class="menu">
            <li class="element"><a routerLink="/paginas/candidatos" routerLinkActive="active">Participantes</a></li>
            <li><a routerLink="/paginas/votos" routerLinkActive="active">A Votar</a></li>
            <li><a routerLink="/paginas/candidatos" routerLinkActive="active">Perfil</a></li>
            <li class="element"><a (click)="desplegar()">Configuracion</a>
                <ul class="menu configElect" #configElect>
                    <li><a routerLink="/paginas/Generales" routerLinkActive="active">Generales</a></li>
                    <li><a routerLink="/paginas/Gestion" routerLinkActive="active">Gestion de Usuarios</a></li>
                    <li (click)="salir()"><a>Cerrar sesion</a></li>
                </ul>
            </li>
        </ul>
    </div>
</div>
